var x32Array = [];
var x1Array = [];
for (var ii = 0; ii < 256; ++ii) 
{
    x32Array[ii] = 0;
    x1Array[ii]  = 0;
}
var gainShift = 0;
if (tray.gain1.value > 0) gainShift = Math.ceil(tray.gain1.value / 6.020599914);
var gainMult = 1;
for (ii = 0; ii < gainShift; ++ii) gainMult = gainMult * 2;
gainMult = Math.exp(0.115129255 * tray.gain1.value) / gainMult;
if (gainMult >  1) gainMult =  1;

x32Array[0] = Math.round(gainMult * 2147483647);
if (x32Array[0] <  0)          x32Array[0] =  0;
if (x32Array[0] >  2147483647) x32Array[0] =  2147483647;

if (gainShift > 31) gainShift = 31;
x32Array[5] = gainShift;

gainShift = 0;
if (tray.gain2.value > 0) gainShift = Math.ceil(tray.gain2.value / 6.020599914);
gainMult = 1;
for (ii = 0; ii < gainShift; ++ii) gainMult = gainMult * 2;
gainMult = Math.exp(0.115129255 * tray.gain2.value) / gainMult;
if (gainMult >  1) gainMult =  1;

x32Array[9] = Math.round(gainMult * 2147483647);
if (x32Array[9] <  0)          x32Array[9] =  0;
if (x32Array[9] >  2147483647) x32Array[9] =  2147483647;

if (gainShift > 31) gainShift = 31;
x32Array[10] = gainShift;

if (tray.phaseShift.value < -180) tray.gainMult.value = -180;
if (tray.phaseShift.value >  180) tray.gainMult.value =  180;
x32Array[1] = Math.round(Math.cos(tray.phaseShift.value * Math.PI / 180.0) * 2147483647);
x32Array[2] = Math.round(Math.sin(tray.phaseShift.value * Math.PI / 180.0) * 2147483647);
if (x32Array[1] < -2147483647) x32Array[1] = -2147483647;
if (x32Array[1] >  2147483647) x32Array[1] =  2147483647;
if (x32Array[2] < -2147483647) x32Array[2] = -2147483647;
if (x32Array[2] >  2147483647) x32Array[2] =  2147483647;

if (tray.lowPassFilterShift.value < 1)  tray.lowPassFilterShift.value = 1;
if (tray.lowPassFilterShift.value > 31) tray.lowPassFilterShift.value = 31;
x32Array[3] = Math.round(tray.lowPassFilterShift.value);
if (x32Array[3] < 1)  x32Array[3] = 1;
if (x32Array[3] > 31) x32Array[3] = 31;

if (tray.derivativeStep.value < 1)   tray.derivativeStep.value = 1;
if (tray.derivativeStep.value > 255) tray.derivativeStep.value = 255;
x32Array[4] = Math.round(tray.derivativeStep.value);
if (x32Array[4] < 1)   x32Array[4] = 1;
if (x32Array[4] > 255) x32Array[4] = 255;

if (tray.limiterWindow1.value <  0)   tray.limiterWindow1.value =  -tray.limiterWindow1.value;
if (tray.limiterWindow1.value >  100) tray.limiterWindow1.value =  100;
x32Array[6] = Math.round(0.01 * tray.limiterWindow1.value * 2147483647);
if (x32Array[6] <  0)          x32Array[6] =  0;
if (x32Array[6] >  2147483647) x32Array[6] =  2147483647;
x32Array[7] = -x32Array[6];

if (tray.limiterWindow2.value <  0)   tray.limiterWindow2.value =  -tray.limiterWindow2.value;
if (tray.limiterWindow2.value >  100) tray.limiterWindow2.value =  100;
x32Array[11] = Math.round(0.01 * tray.limiterWindow2.value * 2147483647);
if (x32Array[11] <  0)          x32Array[11] =  0;
if (x32Array[11] >  2147483647) x32Array[11] =  2147483647;
x32Array[12] = -x32Array[11];

x32Array[8]  = Math.round(0.001 * tray.dacOffset1.value * 2147483647);
x32Array[13] = Math.round(0.001 * tray.dacOffset2.value * 2147483647);
if (x32Array[8] >   2147483647)  x32Array[8] =  2147483647;
if (x32Array[8] <  -2147483647)  x32Array[8] = -2147483647;
if (x32Array[13] >  2147483647) x32Array[13] =  2147483647;
if (x32Array[13] < -2147483647) x32Array[13] = -2147483647;


if (tray.systemOn.value != 0) tray.systemOn.value = 1;
x1Array[0] = 0;
if (tray.systemOn.value == 0) x1Array[0] = 1;

if (tray.highLevelOn1.value != 0) tray.highLevelOn1.value = 1;
x1Array[1] = 0;
if (tray.highLevelOn1.value == 0) x1Array[1] = 1;

if (tray.highLevelOn2.value != 0) tray.highLevelOn2.value = 1;
x1Array[2] = 0;
if (tray.highLevelOn2.value == 0) x1Array[2] = 1;

tray.lpfFreq.value = 1;
for (ii = 0; ii < tray.lowPassFilterShift.value; ++ii) tray.lpfFreq.value = tray.lpfFreq.value * 2;
tray.lpfFreq.value = Math.round(125000000 / (tray.lpfFreq.value * 8)) / 1000;

tray.notchFreq.value = Math.round(125000000 / (32 * tray.derivativeStep.value)) / 1000;
var x32String = '';
var x1String = '';

for (var ii = 0; ii < 256; ++ii) 
{
    x32String = x32String + ii.toString() + '   ' + x32Array[ii].toString() + '\n';
    x1String  = x1String + ii.toString()  + '   ' + x1Array[ii].toString() + '\n';
